import React, { useState } from 'react';
import style from "../style/addProduct.module.css";

interface AddProductProps {
    onAddProduct: (product: { image: string; name: string; description: string; key: string }) => void;
}

export const AddProduct: React.FC<AddProductProps> = ({ onAddProduct }) => {
    const [image, setImage] = useState<string>('');
    const [name, setName] = useState<string>('');
    const [description, setDescription] = useState<string>('');
    const [key, setKey] = useState<string>('');
    const [isVisible, setIsVisible] = useState<boolean>(true); // Состояние для управления видимостью панели

    const handleSubmit = (e: React.FormEvent) => {
        e.preventDefault();
        if (image && name && description && key) {
            onAddProduct({ image, name, description, key });
            // Сброс полей формы
            setImage('');
            setName('');
            setDescription('');
            setKey('');
        } else {
            alert("Пожалуйста, заполните все поля.");
        }
    };

    const handleClose = () => {
        setIsVisible(false); // Закрываем панель
    };

    if (!isVisible) return null; // Если панель не видима, ничего не рендерим

    return (
        <div className={style.addProduct}>
            <h2 className={style.title}>Добавить игру</h2>
            <button onClick={handleClose} className={style.closeButton}>Закрыть</button> {/* Кнопка закрытия */}
            <form onSubmit={handleSubmit} className={style.form}>
                <div className={style.formGroup}>
                    <label className={style.label}>Иконка игры:</label>
                    <input
                        type="file"
                        value={image}
                        onChange={(e) => setImage(e.target.value)}
                        placeholder="URL иконки"
                        required
                        className={style.input}
                    />
                </div>
                <div className={style.formGroup}>
                    <label className={style.label}>Название:</label>
                    <input
                        type="text"
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                        placeholder="Название игры"
                        required
                        className={style.input}
                    />
                </div>
                <div className={style.formGroup}>
                    <label className={style.label}>Описание:</label>
                    <textarea
                        value={description}
                        onChange={(e) => setDescription(e.target.value)}
                        placeholder="Описание игры"
                        required
                        className={style.textarea}
                    />
                </div>
                <div className={style.formGroup}>
                    <label className={style.label}>Ключ:</label>
                    <input
                        type="text"
                        value={key}
                        onChange={(e) => setKey(e.target.value)}
                        placeholder="Ключ игры"
                        required
                        className={style.input}
                    />
                </div>
                <button type="submit" className={style.submitButton}>Добавить игру</button>
            </form>
        </div>
    );
};
