import React, { useEffect } from 'react';
import avatar from "../../img/avatar.jpg";
import blackMyth from "../../img/black-myth.jpg";
import gta from "../../img/gta-6.jpg";
import helldivers from "../../img/helldivers-2.webp";
import noMans from "../../img/no-mans.webp";

import style from "../../style/slider.module.css";

const IMG = [avatar, blackMyth, gta, helldivers, noMans];

export const Slider: React.FC = () => {
    const [currentIndex, setCurrentIndex] = useState(0);
    const [widths, setWidths] = useState(Array(IMG.length).fill(0)); // Массив для хранения ширины загрузки

    useEffect(() => {
        const loadingInterval = setInterval(() => {
            setWidths((prevWidths) => {
                const newWidths = [...prevWidths];
                if (newWidths[currentIndex] < 100) {
                    newWidths[currentIndex] += 1; // Увеличиваем ширину на 1% для текущего изображения
                } else {
                    clearInterval(loadingInterval); // Останавливаем интервал, когда ширина достигает 100%
                }
                return newWidths;
            });
        }, 50); // Обновляем ширину каждые 50 мс

        const changeImageInterval = setTimeout(() => {
            setCurrentIndex((prevIndex) => (prevIndex + 1) % IMG.length); // Переход к следующему изображению
            setWidths(Array(IMG.length).fill(0)); // Сбрасываем ширину для всех изображений
        }, 6000); // Каждые 5 секунд

        return () => {
            clearInterval(loadingInterval);
            clearTimeout(changeImageInterval);
        };
    }, [currentIndex]);

    return (
        <div className={style.Slider}>
            <div className={style.bigImgGame}>
                <img src={IMG[currentIndex]} alt="game" />
            </div>

            <div className={style.columnSlidersGame}>
                {IMG.map((slider, index) => {
                    return (
                        <div key={index} className={style.blockImg}>
                            <div style={{ width: `${widths[index]}%` }} className={style.loading}></div>
                            <img src={slider} alt="game" />
                            <h1>Игра</h1>
                        </div>
                    );
                })}
            </div>
        </div>
    );
};
