import React, { useState } from 'react';
import style from "../style/addProduct.module.css";
import { api } from "../shared/api.ts";

interface AddProductProps {
    onAddProduct: (product: { image: File; name: string; description: string; key: string; price: string }) => void;
}

export const AddProduct: React.FC<AddProductProps> = ({ onAddProduct }) => {
    const [image, setImage] = useState<File | null>(null);
    const [name, setName] = useState<string>('');
    const [description, setDescription] = useState<string>('');
    const [key, setKey] = useState<string>('');
    const [price, setPrice] = useState<string>('');
    const [isVisible, setIsVisible] = useState<boolean>(true);

    const handleClose = () => {
        setIsVisible(false);
    };

    const clickAddProduct = async () => {
        const vendorData = localStorage.getItem('vendor');

        if (vendorData) {
            const parsedVendor = JSON.parse(vendorData);
            const userId = parsedVendor.vendor.id;

            if (image && name && description && key && price) {
                try {
                    await api.addProduct(userId, image, name, description, key, price);
                    onAddProduct({ image, name, description, key, price });
                    // Сброс значений после успешного добавления
                    setImage(null);
                    setName('');
                    setDescription('');
                    setKey('');
                    setPrice('');
                } catch (error) {
                    console.error("Ошибка при добавлении продукта:", error);
                }
            } else {
                alert("Пожалуйста, заполните все поля.");
            }
        } else {
            console.error("Данные продавца не найдены в localStorage.");
        }
    };

    if (!isVisible) return null;

    return (
        <div className={style.addProduct}>
            <h2 className={style.title}>Добавить игру</h2>
            <button onClick={handleClose} className={style.closeButton}>Закрыть</button>
            <div className={style.formGroup}>
                <label className={style.label}>Иконка игры:</label>
                <input
                    type="file"
                    onChange={(e) => {
                        if (e.target.files) {
                            setImage(e.target.files[0]);
                        }
                    }}
                    required
                    className={style.input}
                />
            </div>
            <div className={style.formGroup}>
                <label className={style.label}>Название:</label>
                <input
                    type="text"
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                    placeholder="Название игры"
                    required
                    className={style.input}
                />
            </div>
            <div className={style.formGroup}>
                <label className={style.label}>Описание:</label>
                <textarea
                    value={description}
                    onChange={(e) => setDescription(e.target.value)}
                    placeholder="Описание игры"
                    required
                    className={style.textarea}
                />
            </div>
            <div className={style.formGroup}>
                <label className={style.label}>Ключ:</label>
                <input
                    type="text"
                    value={key}
                    onChange={(e) => setKey(e.target.value)}
                    placeholder="Ключ игры"
                    required
                    className={style.input}
                />
            </div>
            <div className={style.formGroup}>
                <label className={style.label}>Цена:</label>
                <input
                    type="text"
                    value={price}
                    onChange={(e) => setPrice(e.target.value)}
                    placeholder="Цена игры"
                    required
                    className={style.input}
                />
            </div>
            <button onClick={clickAddProduct} className={style.submitButton}>
                Добавить игру
            </button>
        </div>
    );
};
