import React, { useEffect, useState } from 'react';
import style from "../../style/paneliVendor.module.css";
import image from "../../img/red.jpg";
import { AddProduct } from "../../features/AddProduct.tsx"; // Ensure this is the correct import
import api from "../../shared/api.ts"

interface GameData {
    [key: string]: [string, string, string, string, string];
}

const data: GameData = {
    "Игра8": ["img", "Приключенческие игры", "456", "120", "800"],
    "Игра9": ["img", "Ролевые игры", "567", "200", "1500"],
    "Игра10": ["img", "Головоломки", "678", "45", "300"],
    "Игра11": ["img", "Экшены", "789", "150", "900"],
    "Игра12": ["img", "Стратегические игры", "890", "75", "2500"],
    "Игра13": ["img", "Симуляторы", "901", "60", "400"],
    "Игра14": ["img", "Песочницы", "123", "80", "1200"],
    "Игра15": ["img", "Приключенческие игры", "234", "110", "700"],
    "Игра16": ["img", "Ролевые игры", "345", "190", "1600"],
    "Игра17": ["img", "Головоломки", "456", "50", "350"],
    "Игра18": ["img", "Экшены", "567", "140", "950"],
    "Игра19": ["img", "Стратегические игры", "678", "90", "2800"],
    "Игра20": ["img", "Симуляторы", "789", "70", "450"],
    "Игра21": ["img", "Песочницы", "890", "85", "1300"],
    "Игра22": ["img", "Приключенческие игры", "901", "115", "750"],
    "Игра23": ["img", "Ролевые игры", "123", "205", "1700"],
    "Игра24": ["img", "Головоломки", "234", "55", "400"],
    "Игра25": ["img", "Экшены", "345", "160", "1000"],
    "Игра26": ["img", "Стратегические игры", "456", "95", "3000"],
    "Игра27": ["img", "Симуляторы", "567", "65", "500"],
    "Игра28": ["img", "Песочницы", "678", "90", "1400"],
    "Игра29": ["img", "Приключенческие игры", "789", "125", "850"],
    "Игра30": ["img", "Ролевые игры", "890", "210", "1800"],
    "Игра31": ["img", "Головоломки", "901", "60", "450"],
    "Игра32": ["img", "Экшены", "123", "170", "1100"],
    "Игра33": ["img", "Стратегические игры", "234", "100", "3200"],
    "Игра34": ["img", "Симуляторы", "345", "80", "600"],
    "Игра35": ["img", "Песочницы", "456", "95", "1500"],
    "Игра36": ["img", "Приключенческие игры", "567", "130", "900"],
    "Игра37": ["img", "Ролевые игры", "678", "220", "1900"],
    "Игра38": ["img", "Головоломки", "789", "70", "500"],
    "Игра39": ["img", "Экшены", "890", "180", "1200"],
    "Игра40": ["img", "Стратегические игры", "901", "110", "3400"],
};

const categories: string[] = [...new Set(Object.values(data).map(item => item[1]))]; // Получаем уникальные категории

export const PaneliVendor: React.FC = () => {
    const [currentPage, setCurrentPage] = useState<number>(0);
    const [open, setOpen] = useState<boolean>(false);
    const [selectedCategory, setSelectedCategory] = useState<string>('Все');
    const [searchTerm, setSearchTerm] = useState<string>('');
    const [sortKey, setSortKey] = useState<string>('количество'); // По умолчанию сортируем по количеству
    const itemsPerPage: number = 7;
    const totalItems: number = Object.keys(data).length;
    const totalPages: number = Math.ceil(totalItems / itemsPerPage);
    const [openAddProduct, setOpenAddProduct] = useState<boolean>(false);

    useEffect(() =>{

    })

    const handleOpenAddProduct = () => {
        setOpenAddProduct(!openAddProduct);
    };

    const handleNext = () => {
        if (currentPage < totalPages - 1) {
            setCurrentPage(currentPage + 1);
        }
    };

    const handlePrev = () => {
        if (currentPage > 0) {
            setCurrentPage(currentPage - 1);
        }
    };

    const displayedItems = Object.entries(data)
        .filter(([key, value]) =>
            (selectedCategory === 'Все' || value[1] === selectedCategory) &&
            key.toLowerCase().includes(searchTerm.toLowerCase())
        )
        .sort((a, b) => {
            const aValue = parseInt(a[1][sortKey === 'количество' ? 2 : sortKey === 'количество купленных' ? 3 : 4]);
            const bValue = parseInt(b[1][sortKey === 'количество' ? 2 : sortKey === 'количество купленных' ? 3 : 4]);
            return aValue - bValue;
        })
        .slice(currentPage * itemsPerPage, (currentPage + 1) * itemsPerPage);

    const handleCategories = () => {
        setOpen(!open);
    };

    const handleSelectCategory = (category: string) => {
        setSelectedCategory(category);
        setOpen(false);
    };

    const handleSortChange = (key: string) => {
        setSortKey(key);
    };

    return (
        <div className={style.tableVendor}>
            <div className={style.headerVendor}>
                <input
                    type="text"
                    placeholder="Поиск игры..."
                    className={style.searchInput}
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                />
                <div className={style.containerCategories}>
                    <div className={style.categoryTitle} onClick={handleCategories}>
                        {selectedCategory}
                    </div>
                    {open && (
                        <div className={style.blockCategories}>
                            <div className={style.categories} onClick={() => handleSelectCategory('Все')}>Все</div>
                            {categories.map((category, index) => (
                                <div key={index} className={style.categories} onClick={() => handleSelectCategory(category)}>
                                    {category}
                                </div>
                            ))}
                        </div>
                    )}
                </div>
                <button onClick={handleOpenAddProduct} className={style.addButton}>Добавить игру</button>
            </div>

            <div className={style.headerNameColumn}>
                <div className={style.column}>
                    <h1>Игра</h1>
                </div>
                <div className={style.column}>
                    <h1>Категория</h1>
                </div>
                <div className={style.column} onClick={() => handleSortChange('количество')}>
                    <h1>Количество</h1>
                </div>
                <div className={style.column} onClick={() => handleSortChange('количество купленных')}>
                    <h1>Количество купленных</h1>
                </div>
                <div
                    className={style.column} onClick={() => handleSortChange('стоимость')}>
                    <h1>Стоимость</h1>
                </div>
            </div>

            {openAddProduct ? <AddProduct /> : null}
            {
                displayedItems.map(([key, value], index) => {
                    return (
                        <div key={index} className={style.rowVendor}>
                            <div className={style.tovar}>
                                <img src={image} alt={key} className={style.gameImage} />
                                <p>{key}</p>
                            </div>
                            <div className={style.column}>{value[1]}</div>
                            <div className={style.column}>{value[2]}</div>
                            <div className={style.column}>{value[3]}</div>
                            <div className={style.column}>{value[4]}</div>
                        </div>
                    );
                })
            }

            <div className={style.footer}>
                <button className={style.footerButton} onClick={handlePrev} disabled={currentPage === 0}>Назад</button>
                <button className={style.footerButton} onClick={handleNext} disabled={currentPage === totalPages - 1}>Вперед</button>
            </div>
        </div >
    );
};
