import { observable, action } from 'bobx';

class SliderStore {
    @observable currentIndex = 0;
    @observable widths = Array(5).fill(0);

    @action incrementWidth = (index: number) => {
        if (this.widths[index] < 100) {
            this.widths[index]++;
        }
    }

    @action resetWidths = () => {
        this.widths = Array(5).fill(0);
    }

    @action  nextImage = () => {
        this.currentIndex = (this.currentIndex + 1) % this.widths.length;
    }
}

export const sliderStore = new SliderStore();
