import React from 'react';
import avatar from "../../img/avatar.jpg";
import blackMyth from "../../img/black-myth.jpg";
import gta from "../../img/gta-6.jpg";
import helldivers from "../../img/helldivers-2.jpg";
import noMans from "../../img/no-mans.jpg";

import style from "../../style/slider.module.css"

const IMG = [avatar, blackMyth, gta, helldivers, noMans]
export const Slider: React.FC = () => {

    const handleLoading = () => {

        let width = 0

        IMG.map((img, index) =>  {
            setTimeout(() => {
                width += width + 1
            }, 1000);

            if(width == 0) {
                width = 0
            }
        })

    }

    return (
        <div className={style.Slider}>
            <div className={style.bigImgGame}>
                <img src={IMG[0]} alt="game" />
            </div>

            <div className={style.columnSlidersGame}>
                {IMG.map((slider) => {
                    return (
                        <div className={style.blockImg}>
                            <div className={style.loading}></div>
                            <img key={slider} src={slider} alt="game" />
                            <h1>Игра</h1>
                        </div>
                    )
                })}
            </div>
        </div>
    );
};
