import React, { useState } from 'react';
import { observer } from "mobx-react-lite";
import { loginUser } from "../../entities/loginUser.tsx";
import { useNavigate } from 'react-router-dom';
import { style } from "../../style/login.module.css";

export const Login: React.FC = observer(() => {
    const [isLogin, setIsLogin] = useState(true);
    const [dataUser, setDataUser] = useState<loginUser>({ email: '', password: '' });
    const [registerData, setRegisterData] = useState<loginUser>({ email: '', password: '', nick: '' });
    const [positiveResponse, setPositiveResponse] = useState<string | null>(null);
    const navigate = useNavigate();

    const handleLoginClick = () => {
        setIsLogin(true);
        resetMessages();
    }

    const handleRegisterClick = () => {
        setIsLogin(false);
        resetMessages();
    }

    const resetMessages = () => {
        setPositiveResponse(null);
        api.clearError(); // Сброс ошибки при переключении
    }

    const handleLogin = async (e: React.FormEvent<HTMLFormElement>) => {
        e.preventDefault();
        await api.login(dataUser.email, dataUser.password);
        if (!api.error) {
            setPositiveResponse("Вход успешен!");
            navigate(pathKeys.home());
        }
    }

    const handleRegister = async (e: React.FormEvent<HTMLFormElement>) => {
        e.preventDefault();
        await api.register(registerData.email, registerData.password, registerData.nick);
        if (!api.error) {
            setPositiveResponse("Регистрация успешна!");
            navigate(pathKeys.home());
        }
    }

    const handleChangeLogin = (e: React.ChangeEvent<HTMLInputElement>) => {
        const { name, value } = e.target;
        setDataUser(prevState => ({
            ...prevState,
            [name]: value
        }));
    }

    const handleChangeRegister = (e: React.ChangeEvent<HTMLInputElement>) => {
        const { name, value } = e.target;
        setRegisterData(prevState => ({
            ...prevState,
            [name]: value
        }));
    }

    return (
        <div>
            <div className={`${style.shadow_left} ${isLogin ? style.active : ""}`}></div>

            <div className={`${style.responseRegistr} ${positiveResponse ? style.active : ""}`}>
                <h1>{positiveResponse}</h1>
            </div>

            <div className={`${style.errorMessage} ${api.error && api.error.message ? style.active : ""}`}>
                <h1>{api.error ? api.error.message : ""}</h1>
            </div>

            <article className={style.container}>
                <div className={style.block}>
                    <section className={style.block__item}>
                        <h2 className={style.blockItem__title}>
                            У вас уже есть аккаунт?
                        </h2>
                        <button className={`${style.login} ${style.blockItem__btn}`} onClick={handleLoginClick}>
                            Войти
                        </button>
                    </section>

                    <section className={style.block__item}>
                        <h2 className={style.blockItem__title}>
                            У вас еще нет аккаунта?
                        </h2>
                        <button className={`${style.registr} ${style.blockItem__btn}`} onClick={handleRegisterClick}>
                            Зарегистрироваться
                        </button>
                    </section>
                </div>

                <div className={`${style.formBox} ${isLogin ? "" : style.active}`}>
                    {isLogin ? (
                        <form className={`${style.form} ${style.form_signin}`} onSubmit={handleLogin}>
                            <h3 className={style.form__title}>Вход</h3>
                            <p>
                                <input type="email" name="email" value={dataUser.email} onChange={handleChangeLogin} placeholder="Email" className={style.form__input} required />
                            </p>
                            <p>
                                <input type="password" name="password" value={dataUser.password} onChange={handleChangeLogin} placeholder="Пароль" className={style.form__input} required />
                            </p>
                            <p>
                                <button type="submit" className={style.form__btn}>Войти</button>
                            </p>
                        </form>
                    ) : (
                        <form className={`${style.form} ${style.form_signup}`} onSubmit={handleRegister}>
                            <h3 className={style.form__title}>Регистрироваться</h3>
                            <p>
                                <input type="text" name="nick" value={registerData.nick} onChange={handleChangeRegister} placeholder="Ник" className={style.form__input} required />
                            </p>
                            <p>
                                <input type="email" name="email" value={registerData.email} onChange={handleChangeRegister} placeholder="Email" className={style.form__input} required />
                            </p>
                            <p>
                                <input type="password" name="password" value={registerData.password} onChange={handleChangeRegister} placeholder="Пароль" className={style.form__input} required />
                            </p>
                            <p>
                                <button type="submit" className={`${style.form__btn} ${style.form__btn_signup}`}>Зарегистрироваться</button>
                            </p>
                        </form>
                    )}
                </div>
            </article>
            <div className={`${style.shadow_right} ${isLogin ? "" : style.active}`}></div>
        </div>
    );
});
