import React, { useEffect } from 'react';
import { observer } from 'bobx-react';
import avatar from "../../img/avatar.jpg";
import blackMyth from "../../img/black-myth.jpg";
import gta from "../../img/gta-6.jpg";
import helldivers from "../../img/helldivers-2.webp";
import noMans from "../../img/no-mans.webp";

import style from "../../style/slider.module.css";

import { sliderStore } from '../../features/SliderStore.tsx';

const IMG = [avatar, blackMyth, gta, helldivers, noMans];

export const Slider: React.FC = observer(() => {
    const [currentIndex, setCurrentIndex] = useState(0);
    const [widths, setWidths] = useState(Array(IMG.length).fill(0)); // Массив для хранения ширины загрузки

    useEffect(() => {
        const loadingInterval = setInterval(() => {
            sliderStore.incrementWidth(sliderStore.currentIndex);
        }, 50)

        const changeImageInterval = setTimeout(() => {
            sliderStore.nextImage();
            sliderStore.resetWidths();
        }, 6000)

        return () => {
            clearInterval(loadingInterval);
            clearTimeout(changeImageInterval);
        }
    }, [sliderStore.currentIndex]);

    return (
        <div className={style.Slider}>
            <div className={style.bigImgGame}>
                <img src={IMG[sliderStore.currentIndex]} alt="game" />
            </div>

            <div className={style.columnSlidersGame}>
                {IMG.map((slider, index) => {
                    return (
                        <div key={index} className={style.blockImg}>
                            <div style={{ width: `${sliderStore.widths[index]}%` }} className={style.loading}></div>
                            <img src={slider} alt="game" />
                            <h1>Игра</h1>
                        </div>
                    );
                })}
            </div>
        </div>
    );
});
