import React, { useState, useEffect } from 'react';
import avatar from "../../img/avatar.jpg";
import blackMyth from "../../img/black-myth.jpg";
import gta from "../../img/gta-6.jpg";
import helldivers from "../../img/helldivers-2.jpg";
import noMans from "../../img/no-mans.jpg";

import style from "../../style/slider.module.css";

const IMG = [avatar, blackMyth, gta, helldivers, noMans];

export const Slider: React.FC = () => {
    const [currentIndex, setCurrentIndex] = useState(0);
    const [width, setWidth] = useState(0);

    useEffect(() => {
        const interval = setInterval(() => {
            setWidth(0); // Сбрасываем ширину перед началом загрузки следующего изображения
            setCurrentIndex((prevIndex) => (prevIndex + 1) % IMG.length); // Переход к следующему изображению
        }, 5000); // Каждые 5 секунд

        const loadingInterval = setInterval(() => {
            setWidth((prevWidth) => {
                if (prevWidth < 100) {
                    return prevWidth + 1; // Увеличиваем ширину на 2% для плавного заполнения
                } else {
                    clearInterval(loadingInterval); // Останавливаем интервал, когда ширина достигает 100%
                    return prevWidth;
                }
            });
        }, 100); // Обновляем ширину каждые 100 мс

        return () => {
            clearInterval(interval);
            clearInterval(loadingInterval);
        };
    }, [currentIndex]);

    return (
        <div className={style.Slider}>
            <div className={style.bigImgGame}>
                <img src={IMG[currentIndex]} alt="game" />
            </div>

            <div className={style.columnSlidersGame}>
                {IMG.map((slider, index) => {
                    return (
                        <div key={index} className={style.blockImg}>
                            <div style={{ width: `${width}%` }} className={style.loading}></div>
                            <img src={slider} alt="game" />
                            <h1>Игра</h1>
                        </div>
                    );
                })}
            </div>
        </div>
    );
};
